function stickyHeader() {
  // from desktop to wide
  if (w > 90 && $('.header-nav-hamburger.icon-nav').is(':hidden') && $('.header-nav-hamburger.icon-close').is(':hidden') && !wideUp) {
    // add sticky
    $('.header-nav, .header-ad').addClass('sticky');
  // from wide up
} else if (w > 127 && wideUp) {
    // add sticky
    $('.header-nav, .header-ad').addClass('sticky');
  } else {
    // remove sticky
    $('.header-nav, .header-ad').removeClass('sticky');
  }
  resizeSearch();
}

// resize header search bar on desktop
function resizeSearch() {
  // if search is visible and
  // header is either desktop-up OR sticky
  if ($('.header-nav-search').is(':visible') && desktopUp) {
    // get width of the nav links section
    var navWidth = $('.header-nav-links').width();
    // get width of subscribe button
    var subWidth = $('.header-nav-subscribe').width();
    // calc search
    var searchWidth = navWidth - subWidth - 12;
    // set search width
    $('.header-nav-search').css('width', searchWidth + 'px');
  } else {
    // reset search
    $('.header-nav-search').css('width', '');
  }
}

// shut down mobile nav on resize up
function cancelNav() {
  // if both the mobile nav and the desktopUp nav are visible
  if ($('.mobile-nav').is(':visible') && desktopUp) {
    // shut down the mobile nav
    $('.mobile-nav').fadeOut(100);
    // reset the hamburger & close icons
    $('.header-nav-hamburger.icon-close').css('display', '');
    $('.header-nav-hamburger.icon-nav').css('display', '');
  }
}

// on typekit render
function typekitWait() {
  var dotElements =
    '.card-excerpt-wrap';

  var dotElementsReadMore =
    '.blog-stack-excerpt-wrap';

  var matchHeightElements =
    '.home-stack-card, .footer-touts .tout, .main-archive-issue, .history-gallery-figure-wrap';

  var typekitListen = setInterval(function() {
    if (typekitUp === true) {
      $(dotElements).dotdotdot({
        ellipsis: ' ...',
        watch: 'window'
      });
      $(dotElementsReadMore).dotdotdot({
        ellipsis: ' ... ',
        after: 'a.read-more',
        watch: 'window'
      });
      $(matchHeightElements).matchHeight();

      if ($('.of-the-day_interview').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_interview').matchHeight();
        }
      }

      if ($('.of-the-day_art').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_art').matchHeight();
        }
      }

      if ($('.of-the-day_audio').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_audio').matchHeight();
        }
      }

      if ($('.of-the-day_podcast').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_podcast').matchHeight();
        }
      }

      if ($('.of-the-day_video').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_video').matchHeight();
        }
      }

      if ($('.base_event').length) {
        if (desktopUp) {
          $('.landing-stack-card').matchHeight();
        } else {
          $('.base_event').matchHeight();
        }
      }

      clearInterval(typekitListen);
    }
  }, 50);
}

