// set tools to fixed position
function pinArticleTools() {
  // set position to fixed
  $('.article-tools_desktop').css('position', 'fixed');
  // set top value
  $('.article-tools_desktop').css('top', articleToolsTopTrigger + 'px');
  // set left value
  $('.article-tools_desktop').css('left', articleToolsLeftPosition + 'px');
  $('.article-tools_desktop').css('width', 'auto');
}

// toggle sticky article tools
function stickyArticleTools() {
  articleToolsHeight = $('.article-tools_desktop').outerHeight();
  articleToolsTopPosition = $('.article-tools_desktop').offset().top - w;
  articleToolsBottomPosition = articleToolsTopPosition + articleToolsHeight;
  articleBodyBottom = articleBodyTop + articleBodyHeight - articleToolsHeight;

  // if top position crosses top boundary
  if($('.article-tools_desktop').css('position') === 'static' && articleToolsTopPosition < articleToolsTopTrigger) {
    // set tools position to fixed
    pinArticleTools();
  }
  // if top boundary crosses back across fixed top position
  if($('.article-tools_desktop').offset().top <= articleToolsHome) {
    // reset position to static
    $('.article-tools_desktop').css('position', 'static');
    $('.article-tools_desktop').css('width', '');
  }
  // if bottom boundary of article crosses bottom of tools
  if(articleToolsBottomTrigger <= articleToolsBottomPosition) {
    // set position to absolute
    $('.article-tools_desktop').css('position', 'absolute');
    // set top value
    $('.article-tools_desktop').css('top', articleBodyBottom + 'px');
  }
  // if tools pass back past top boundary
  if($('.article-tools_desktop').css('position') == 'absolute' && articleToolsTopPosition > articleToolsTopTrigger) {
    // set tools position back to fixed
    pinArticleTools();
  }
}

// fix left position of sticky tools
function lateralArticleTools() {
  // if article tools isn't static, update its left position just in case
  if($('.article-tools_desktop').css('position') !== 'static') {
    // infer left position from width of article body
    articleToolsLeftPosition = articleBodyWidth + 35;
    // set updated left position
    $('.article-tools_desktop').css('left', articleToolsLeftPosition + 'px');
  }
}

// fix left position of sticky tools
function lateralArticleToolsMax() {
  // if article tools isn't static, update its left position just in case
  if($('.article-tools_desktop').css('position') !== 'static') {
    // infer left position from width of article body
    articleToolsLeftPosition = articleBodyWidth + 35 + ( ($('.wrap').outerWidth(true) - $('.wrap').outerWidth()) / 2);
    // set updated left position
    $('.article-tools_desktop').css('left', articleToolsLeftPosition + 'px');
  }
}


function articleToolsHomeCond() {

  if ($('.article-tools_desktop').css('position') === 'static') {
    articleToolsHome = articleToolsTopPosition;
  } else {
    articleToolsHome = $('.article-body').offset().top;
  }
}

