$(document).ready(function() {


  if ($('.article-body-wrap').length) {

    articleToolsHome = $('.article-tools_desktop').offset().top;

    $(window).on('load', function() {
      // if desktop up
      if(desktopUp) {

        articleBodyTop = $('.article-body-wrap').offset().top;
        articleBodyHeight = $('.article-body-wrap').outerHeight();
        articleToolsTopTrigger = $('.header-nav').outerHeight() + 16;
        articleToolsLeftPosition = $('.article-tools_desktop').offset().left;
        articleToolsBottomTrigger = articleBodyTop + articleBodyHeight - w;

        stickyArticleTools();
      }
    });

    $(window).on('scroll', function() {
      if(!didScrollArticle) {
        timerScrollArticle = setInterval(function() {
          if (didScrollArticle) {
            didScrollArticle = false;
            clearTimeout(timerScrollArticle);

            if(desktopUp) {

              w = $(window).scrollTop();
              articleToolsTopTrigger = $('.header-nav').outerHeight() + 16;
              articleToolsBottomTrigger = articleBodyTop + articleBodyHeight - w;

              articleToolsHomeCond();
              stickyArticleTools();
            }
          }
        }, 50);
      }
      didScrollArticle = true;
    });

    $(window).on('resize', function() {
      if(!didResizeArticle) {
        timerResizeArticle = setInterval(function() {
          if (didResizeArticle) {
            didResizeArticle = false;
            clearTimeout(timerResizeArticle);

            // desktopUp = $('.header-nav-thedaily').is(':visible');
            // wideUp = $('.header-nav-subscribe-cover').is(':visible');
            maxUp = window.innerWidth >= 1328;

            if(desktopUp) {
              articleBodyTop = $('.article-body-wrap').offset().top;
              articleBodyHeight = $('.article-body-wrap').outerHeight();
              articleToolsTopTrigger = $('.header-nav').outerHeight() + 16;
              articleToolsLeftPosition = $('.article-tools_desktop').offset().left;
              articleToolsBottomTrigger = articleBodyTop + articleBodyHeight - w;
              articleBodyWidth = $('.article-body').outerWidth(true);

              stickyArticleTools();

              if(!maxUp) {
                lateralArticleTools();
              }
              if(maxUp) {
                lateralArticleToolsMax();
              }
            }
          }
        }, 50);
      }
      didResizeArticle = true;
    });
  }

  // right rail issue dropdown
  $('.rail-issue-category').on('click',function() {
    $('.rail-issue-article', this).slideToggle(200, function() {
      $(this).parent().prev().find('.icon-caret-small').toggleClass('active');
    });
  });
});
