function getRailNavBoundaries() {

  if (!tabletUp) {
    railNavTopBoundary = headerHeight;
  } else {
    railNavTopBoundary = headerHeight + 32;
  }
  railNavBottomBoundary = mainTop + mainHeight - w;
}

// set tools to fixed position
function pinRailNav() {

  $('.rail_left').css({
    'position': 'fixed',
    'top': railNavTopBoundary + 'px'
  });
  $('.rail_left').addClass('sticky');

  if (!tabletUp) {
    $('.main_right').css('padding-top', railLeftHeight);
  } else {
    $('.main_right').addClass('sticky');
  }
}

// toggle sticky rail nav
function stickyRailNav() {

  railNavTopPosition = railNavTop - w;
  railNavBottomPosition = railNavTopPosition + railLeftHeight;
  mainBottom = mainTop + mainHeight - railLeftHeight;

  // if top position crosses top boundary
  if($('.rail_left').css('position') === 'static' && railNavTopPosition < railNavTopBoundary) {
    pinRailNav();
  }

  // if top boundary crosses back across fixed top position
  if($('.rail_left').offset().top <= railNavHome) {
    $('.rail_left').css({
      'position': 'static',
      'width': ''
    });
    $('.rail_left, .main_right').removeClass('sticky');
    $('.main_right').css('padding-top', '');
  }

  if (tabletUp) {
    // if bottom boundary of article crosses bottom of tools
    if(railNavBottomBoundary <= railNavBottomPosition) {
      $('.rail_left').css({
        'position': 'absolute',
        'top': mainBottom + 'px'
      });
    }

    // if tools pass back past top boundary
    if($('.rail_left').css('position') == 'absolute' && railNavTopPosition > railNavTopBoundary) {
      pinRailNav();
    }
  }
}

function railNavHomeCond() {

  if ($('.rail_left').css('position') === 'static') {
    railNavHome = railNavTop;
  } else {
    railNavHome = mainTop;
    if (tabletUp && !hadadaListener) {
      getRailNavBoundaries();
      pinRailNav();
    }
  }
}

// update RailNavHome
function updateRailNavHome() {

  if ($('.rail-nav.magazine, .rail-nav.authors').length) {

    // as window crosses tablet-up
    if (tabletUp && !hadadaListener) {
      railNavHomeCond();

    // if window is staying above tablet-up
    } else if (tabletUp && hadadaListener) {

      currentIssueHeight = $('.main-current-issue').outerHeight();

      // if you cross desktop or wide
      if (currentIssueListener !== currentIssueHeight) {
        currentIssueListener = currentIssueHeight;
        railNavHomeCond();
      }

    // as window crosses tablet down
    } else if (hadadaListener !== tabletUp) {
      getRailNavBoundaries();
      railNavHomeCond();
      $('.rail_left').css('top', railNavTopBoundary + 'px');
    }
    hadadaListener = tabletUp;

  // for general landing pages
  } else if ($('.main_landing').length) {

    // as window crosses tablet-up
    if (tabletUp && !hadadaListener) {
      railNavHomeCond();

    // if window is staying above tablet-up
    } else if (tabletUp && hadadaListener) {
      // currentIssueHeight = $('.main-current-issue').outerHeight();
      //
      // // if you cross desktop or wide
      // if (currentIssueListener !== currentIssueHeight) {
      //   currentIssueListener = currentIssueHeight;
        railNavHomeCond();
      // }

    // as window crosses tablet down
    } else if (hadadaListener !== tabletUp) {
      getRailNavBoundaries();
      railNavHomeCond();
      $('.rail_left').css('top', railNavTopBoundary + 'px');
    }
    hadadaListener = tabletUp;
  }
}

